using System;
using System.Linq;
using AdventOfCode.Common;

namespace AdventOfCode.Year2015
{
    public class Day01 : IChallenge
    {
        public string Input => _INPUT;

        public string SolvePartOne()
        {
            return Input.Select(_ => (_ == '(' ? 1 : -1)).Sum().ToString();
        }

        public string SolvePartTwo()
        {
            var floor = 0;
            foreach (var (value, index) in Input.Select((value, index) => (value, index + 1)))
            {
                floor += (value == '(' ? 1 : -1);
                if (floor == -1)
                    return index.ToString();
            }

            throw new InvalidOperationException("Sante never reaches the basement");
        }

